INSERT INTO `wadwettay_blog_latest_news` VALUES (16375136,'Додон: Идея утилизации боеприпасов в Приднестровье – мудрый и смелый шаг со стороны РФ','Президент Молдовы заявил, что вопрос об утилизацию боеприпасов будет вынесен на переговоры по Приднестровью.',16346190,1),(16375121,'Песков рассказал о недовольстве Путина ситуацией в угольной отрасли','Президент России считает недопустимой ситуацию, когда работники, выполняющие одну и ту же работу, получают разную зарплату, сказал пресс-секретарь главы государства.',16346148,0),(16375116,'Токаев: Улытау в Казахстане должен стать центром притяжения туристов','Президент Казахстана поручил разработать туристическое приложение c информацией о стране.',16345742,0),(16375111,'Зеленский рассказал, за что любит Украину','Президент Украины выступил на праздновании Дня Независимости.',16346142,1),(16375107,'Шойгу передал Додону рассекреченные документы об освобождении Молдовы от фашистов','Министр обороны России сообщил молдавскому президенту, что российские военные создали мультимедийный раздел с архивными документами, посвященными 75-й годовщине освобождения Кишинева от немец',16346152,1),(16375168,'Пять младенцев из Дагестана и Чечни доставили в Петербург спецбортом МЧС','Малыши появились на свет с врожденными патологиями, угрожающими жизни. Перинатальные центры Махачкалы и Грозного не могли им помочь.',16341378,0),(16375165,'Чебоксарам – 550: столица Чувашии отметила юбилей','Торжества в честь юбилея города в Чебоксарах продолжаются уже неделю. Национальные фестивали, кулинарные шоу, различные концерты и театрализованные экскурсии. Все абсолютно бесплатно.',16346187,1),(16375161,'После реставрации в Кишиневе открыли памятник воинам-освободителям Молдовы','В Молдове отмечают 75-ю годовщину освобождения от немецко-фашистских захватчиков. 24 августа 1944 года советская армия оттеснила войска Вермахта от границ республики.',16346184,1);
